mapper.select:
select count(1)
from folder
where username = #{username}

mapper.update:
update folder set guest = #{guest}
where username =
#{username} and seq = #{seq}

update folder set locked = #{locked}
where username =
#{username} and seq = #{seq}

mapper.select:
update folder set shared =
#{shared}
where username = #{username} and seq = #{seq}
returning
username, seq, name, locked, shared, guest

update folder set name =
#{name}
where username = #{username} and seq = #{seq}
returning name
