mapper.select:
select "number", email,
username, folder, thumbnail, original, filename, price,
createdt,
charged, expiredt
from "order"
where "number" = #{number}
and email =
#{email}
and expiredt >= #{expiredt}
order by createdt asc

mapper.update:
update "order" set charged = #{charged}
where "number"
= #{number} and email = #{email}
